import React from 'react';
import { Card, Button } from 'react-bootstrap';
import CenteredContainer from './CenteredContainer'; // Import the CenteredContainer component

function BookingComponent({ cart }) {
  const totalPrice = cart.reduce((sum, book) => sum + book.bookPrice, 0);
  const totalBooks = cart.length;

  return (
    <CenteredContainer>
      <div className='container mb-4'>
        <h1>Booking</h1>
        <Card style={{ width: '18rem', margin: '20px' }}>
          <Card.Body>
            <Card.Title>Booking Information</Card.Title>
            <Card.Text style={{ fontSize: '18px', marginBottom: '10px', fontWeight: 'bold' }}>
              <p>Total Price: ${totalPrice}</p>
              <p>Total Books to Buy: {totalBooks}</p>
            </Card.Text>
            <Button variant="success">Continue</Button>
          </Card.Body>
        </Card>
      </div>
    </CenteredContainer>
  );
}

export default BookingComponent;
