import { useEffect, useState } from "react";
import Navbarcomponent from "../navbar";
import axios from "axios";
import { Table } from "react-bootstrap";
import { getHistory } from "../../store/actions/allbooks";

function PreviousOrders() {
  const [orders, setOrders] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [history,setHistory]=useState();
  const dispatch = useDispatch();
    let { list } = useSelector((state) => state.history);
    useEffect(() => {
      dispatch(getHistory());
  }, [dispatch]);




  // useEffect(() => {
  //   // Fetch orders from the API
  //   axios.get("http://localhost:8182/customerBook/customerid/15")
  //     .then((response) => {
  //       setOrders(response.data);
  //       calculateTotalAmount(response.data);
  //     })
  //     .catch((error) => console.error("Error fetching orders:", error));
  // }, []);

  const calculateTotalAmount = (orders) => {
    const total = orders.reduce((acc, order) => acc + order.amount, 0);
    setTotalAmount(total);
  };

  return (
    <div>
      <div>
        <Navbarcomponent />
      </div>
      <h1>Previous orders</h1>
      <Table>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Issue Date</th>
            <th>Return Date</th>
            <th>Fine</th>
            {/* Add more columns based on your data structure */}
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.issueDate}</td>
              <td>{order.returnDate}</td>
              <td>{order.amount}</td>
              {/* Add more cells based on your data structure */}
            </tr>
          ))}
          <tr>
            <td colSpan="3"></td>
            <td>Total Amount: {totalAmount}</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
}

export default PreviousOrders;
