import React, { useEffect, useState } from "react";
import Navbarcomponent from "../navbar";
import axios from "axios";
import { Card, Navbar, Row } from "react-bootstrap";
import { useNavigate, useParams, useLocation } from "react-router";

function BookingStatus() {
  const [customerBook, setCustomerBook] = useState([]);
  const { id } = useParams();
  const [totalBooks, setTotalBooks] = useState(0);
  const [totalAmount, setTotalAmount] = useState(0);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const bookIds = new URLSearchParams(location.search).get("bookIds");
    if (bookIds) {
      const idsArray = bookIds.split(",");
      axios.get(`http://localhost:8182/customerBook/customerid/${id}`)
        .then((response) => {
          const filteredBooks = response.data.filter((book) => idsArray.includes(book.id.toString()));
          setCustomerBook(filteredBooks);
          calculateTotals(filteredBooks);
        })
        .catch((error) => console.error("Error fetching customer books:", error));
    } else {
      navigate(`/customer/dashboard`);
    }
  }, [id, location.search, navigate]);

  const calculateTotals = (data) => {
    let uniqueBooks = new Set();
    let amount = 0;

    data.forEach((cb) => {
      uniqueBooks.add(cb.id);
      amount += cb.amount;
    });

    setTotalBooks(uniqueBooks.size);
    setTotalAmount(amount);
  };

  return (
    <div>
      <Navbar bg="dark" data-bs-theme="dark" style={{ padding: 10, marginBottom: 0 }}>
        <Navbar.Brand><h2 style={{ margin: 0 }}>INFOLIBRA</h2></Navbar.Brand>
        <Navbar.Collapse className="justify-content-end">
          {
            localStorage.getItem('isLoggedIn') ?
              <>
                <Navbar.Text>
                  Signed in as: <span style={{ color: "white" }}>{localStorage.getItem('username')}</span>
                </Navbar.Text>
                &nbsp;&nbsp;&nbsp;
                <button className="btn btn-info btn-sm ml-4" onClick={() => { localStorage.clear(); navigate('/auth/login?msg=you have logged out..') }}>
                  Logout
                </button>
              </>
              :
              <button className="btn btn-primary" onClick={() => navigate('/auth/login')}>Login</button>
          }
        </Navbar.Collapse>
      </Navbar>
      <Row>
        {customerBook.map((cb, index) => (
          <div key={index} className="col-md-4 mb-4">
            <Card
              style={{
                width: "450px",
                border: "2px solid #3498db",
                borderRadius: "0.5rem",
                marginBottom: "30px",
                boxShadow: "0 8px 16px rgba(52, 152, 219, 0.2)",
                transition: "transform 0.3s ease-in-out",
                cursor: "pointer",
              }}
              onMouseEnter={(e) => (e.target.style.transform = "scale(1.05)")}
              onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
            >
              <Card.Body>
                <Card.Title
                  style={{
                    fontSize: "2rem",
                    fontWeight: "bold",
                    marginBottom: "15px",
                    color: "#3498db",
                  }}
                >
                  BookId: {cb.id}
                </Card.Title>
                <hr />
                <div>
                  <div>
                    <span style={{ fontWeight: "bold", fontSize: "1.2rem" }}>Name:</span>{" "}
                    {cb.book.bookTitle}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold", fontSize: "1.2rem" }}>Issue Date:</span>{" "}
                    {cb.issueDate}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold", fontSize: "1.2rem" }}>Return Date:</span>{" "}
                    {cb.returnDate}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold", fontSize: "1.2rem" }}>Fine:</span> ${cb.amount}
                  </div>
                </div>
              </Card.Body>
            </Card>
          </div>
        ))}
      </Row>
      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <h4>Total Books: {totalBooks}</h4>
        <h4>Total Amount: ${totalAmount}</h4>
      </div>
      <h1>Booked Successfully</h1>
    </div>
  );
}

export default BookingStatus;
