import { useEffect, useState } from "react";
import Navbarcomponent from "../navbar";
import axios from "axios";
import { Table } from "react-bootstrap";
import { useParams } from "react-router";

function PreviousOrders() {
  const { cid } = useParams();

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:8182/customerBook/customerid/${cid}`)
      .then((response) => setOrders(response.data))
      .catch((error) => console.error("Error fetching orders:", error));
  }, [cid]);

  const calculateTotalFine = () => {
    return orders.reduce((total, order) => total + order.amount, 0);
  };

  return (
    <div
      style={{
        padding: 20,
        minHeight: "100vh",
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#f8f9fa",
        backgroundImage:
          'url("https://img.freepik.com/premium-photo/book-stack-library-blurred-bookshelf-background-education-education-background-back_756748-17160.jpg")',
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <h1
        style={{
          textAlign: "center",
          padding: "20px 0",
          borderBottom: "1px solid #dee2e6",
        }}
      >
        Previous orders
      </h1>
      <div
        style={{
          width: "80%",
          marginTop: 20,
          backgroundColor: "rgba(255, 255, 255, 0.8)",
          borderRadius: 10,
          overflow: "hidden",
          boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <Table className="table-transparent">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Issue Date</th>
              <th>Return Date</th>
              <th>Fine</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>{order.id}</td>
                <td>{order.issueDate}</td>
                <td>{order.returnDate}</td>
                <td>{order.amount}</td>
              </tr>
            ))}
            <tr>
              <td colSpan="3"></td>
              <td>Total Fine: {calculateTotalFine()}</td>
            </tr>
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default PreviousOrders;
