import { useEffect, useState } from "react";
import { Link, useNavigate, useSearchParams } from "react-router-dom";
import NavbarComponent from "../navbar";
import axios from "axios";
import { Button, Card, ListGroup, Nav, Row } from "react-bootstrap";

function CustomerDashboard() {
  const navigate = useNavigate();
  const [param] = useSearchParams();
  const [qStr, setQstr] = useState('');
  const [book, setBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
 
  useEffect(() => {
    axios.get('http://localhost:8182/Book/all')
      .then(response => setBooks(response.data))
      .catch(error => setMsg('Error in Fetching books'));

    axios.get('http://localhost:8182/category/getall')
      .then(response => setCategories(response.data))
      .catch(error => setMsg('Error in Fetching categories'));
  }, []);

  const redirectToBookDetails = (bookId) => {
    navigate(`/books/${bookId}`);
  };

  const searchBooks = (str) => {
    console.log('Search function in parent component called: ' + str);
    setQstr(str);
  };

  const getBookCategory = async (id) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:8182/customer/getbycategoryid?id=${id}`);
      console.log('API response:', response);
      setBooks(response.data || []);
    } catch (error) {
      setMsg('Error in Fetching books by category');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <NavbarComponent func={searchBooks} />
      <Row style={{ padding: 20 }}>
        <div>
          <Card style={{ width: "27rem", margin: 20 }}>
            <Card.Header style={{ backgroundColor: "#3498db", color: "#fff" }}>
              <h3 style={{ fontSize: "1.5rem", fontFamily: "Arial, sans-serif" }}>Categories</h3>
            </Card.Header>
            <ListGroup>
              {categories.map((c, index) => (
                <div key={index}>
                  <ListGroup.Item>
                    <Nav.Link onClick={() => { getBookCategory(c.id); }}>
                      <h4 style={{ color: "#2c3e50", fontWeight: "bold", fontFamily: "Verdana, sans-serif" }}>
                        {c.name}
                      </h4>
                    </Nav.Link>
                  </ListGroup.Item>
                </div>
              ))}
            </ListGroup>
          </Card>
        </div>

        <div className="col-md-8">
          <Row>
            {book.map((b, index) => (
              <div key={index} className="col-md-4 mb-4">
                <Card style={{ width: '400px', backgroundColor: 'rgba(255, 255, 255, 0.9)' }}>
                  <Card.Body>
                    <Link to={`/books/${b.id}`}>
                      <Card.Title style={{ color: "#e74c3c", fontSize: "1.2rem", fontFamily: "Georgia, serif" }}>
                        {b.bookTitle}
                      </Card.Title>
                    </Link>
                    <hr />
                  </Card.Body>
                  <div>
                    <div style={{ display: 'flex', flexDirection: 'column', marginBottom: '10px' }}>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#27ae60" }}>Rating:</span> {b.rating}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#3498db" }}>Author:</span>
                        <Link to={`/author/${b.author}`} style={{ color: "#3498db", textDecoration: "none", fontFamily: "Helvetica, sans-serif" }}>
                          {b.author}
                        </Link>
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#e67e22" }}>Category:</span> {b.category.name}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#f39c12" }}>Price:</span> {b.bookPrice}
                      </div>
                    </div>
                    <Button style={{ width: 200, alignSelf: "center", margin: 15, backgroundColor: "#3498db", color: "#fff", fontFamily: "Arial, sans-serif" }} variant="outline-primary" href='/auth/login'>Add to cart</Button>
                  </div>
                </Card>
              </div>
            ))}
          </Row>
        </div>
      </Row>
    </div>
  );
}

export default CustomerDashboard;
