// AuthorDetails.js
import React, { useEffect, useState } from "react";
import { useParams, useSearchParams } from "react-router-dom";
import axios from "axios";

function AuthorDetails() {
    const [param] = useSearchParams();
    const [books,setBooks] = useState([]);
    useEffect(()=>{
        console.log('Book ID:', param.get('bid'));
        axios.get(`http://localhost:8182/Book/getbybook/`+ param.get('bid'))
        .then(response=>setBooks(response.data)).catch(error => console.error("Error fetching book details:", error));
    }, [param]);
    return(
        <div>
              {books.map((b, index) => (
                <div key={index} >
                      {b.bookTitle} 
                </div>
              ))}
            </div>
       
    )
}

export default AuthorDetails;
