// CartComponent.js

import React from 'react';
import { Card, Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function CartComponent({ cart }) {
  return (
    <div>
      <h2>Cart</h2>
      {/* <ul>
        {cart.map((b, index) => (
          <li key={index}>{b.bookTitle}{
            
          }</li>
        ))}
      </ul> */}
      <div className="col-md-8">
          <Row>
            {cart.map((b, index) => (
              <div key={index} className="col-md-4 mb-4">
                <Card style={{ width: '400px', backgroundColor: '#your_card_background_color_here' }}>
                  <Card.Body style={{ backgroundColor: '#your_card_body_background_color_here' }}>
                    <Link to={'/books?id='+b.id} >
                      <Card.Title>
                        {b.bookTitle}
                      </Card.Title>
                    </Link>
                    <hr />
                    <div>
                      <div style={{ display: 'flex', flexDirection: 'column', marginBottom: '10px' }}>
                        <div>
                          <span style={{ fontWeight: 'bold' }}>Rating:</span> {b.rating}
                        </div>
                        <div>
                          <span style={{ fontWeight: 'bold' }}>Author:</span>
                          <Link to={'/authors?id='+b.id}>
                      {b.author}
                    </Link>
                        </div>
                        <div>
                          <span style={{ fontWeight: 'bold' }}>Category:</span> {b.category.name}
                        </div>
                        <div>
                          <span style={{ fontWeight: 'bold' }}>Price:</span> {b.bookPrice}
                        </div>
                      </div>
                     
                    </div>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </Row>
        </div>
    </div>
  );
}

export default CartComponent;
