// reducers.js
const initialState = {
    orders: [],
  };
  
  const rootReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_ORDERS':
        return {
          ...state,
          orders: action.payload,
        };
      default:
        return state;
    }
  };
  
  export default rootReducer;
