import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';

function AdminDashboard() {
  const [books, setBooks] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [customerBookings, setCustomerBookings] = useState([]);
  const [showBooks, setShowBooks] = useState(false);
  const [showCustomers, setShowCustomers] = useState(false);
  const [showCustomerBookings, setShowCustomerBookings] = useState(false);
  const [selectedBookId, setSelectedBookId] = useState(null);
  const [customersByBookId, setCustomersByBookId] = useState([]);
  const [showCustomersByBookId, setShowCustomersByBookId] = useState(false);

  useEffect(() => {
    fetchBooks();
    fetchCustomers();
    fetchCustomerBookings();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get("http://localhost:8182/Book/all");
      setBooks(response.data);
      setShowBooks(true);
    } catch (error) {
      console.error("Error fetching books:", error.message);
    }
  };

  const hideBooks = () => {
    setBooks([]);
    setShowBooks(false);
  };

  const fetchCustomers = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customer/getall");
      setCustomers(response.data);
      setShowCustomers(true);
    } catch (error) {
      console.error("Error fetching customers:", error.message);
    }
  };

  const hideCustomers = () => {
    setCustomers([]);
    setShowCustomers(false);
  };

  const fetchCustomerBookings = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customerBook/getall");
      setCustomerBookings(response.data);
      setShowCustomerBookings(true);
    } catch (error) {
      console.error("Error fetching customer bookings:", error.message);
    }
  };

  const hideCustomerBookings = () => {
    setCustomerBookings([]);
    setShowCustomerBookings(false);
  };

  const fetchCustomersByBookId = async (bookId) => {
    try {
      const response = await axios.get(`http://localhost:8182/customerBook/customerid/${bookId}`);
      setCustomersByBookId(response.data);
      setShowCustomersByBookId(true);
    } catch (error) {
      console.error("Error fetching customers by book id:", error.message);
    }
  };

  const hideCustomersByBookId = () => {
    setCustomersByBookId([]);
    setShowCustomersByBookId(false);
  };

  const handleBookClick = (bookId) => {
    setSelectedBookId(bookId);
    fetchCustomersByBookId(bookId);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Admin Dashboard</h1>

      <button
        className={`btn ${showBooks ? "btn-danger" : "btn-success"} mb-2`}
        onClick={showBooks ? hideBooks : fetchBooks}
      >
        {showBooks ? `Hide Books (${books.length})` : `Show Books (${books.length})`}
      </button>

      {showBooks && (
        <div className="d-flex flex-wrap">
          {books.map((book) => (
            <Card
              key={book.id}
              className="m-2"
              style={{ width: "18rem", cursor: "pointer" }}
              onClick={() => handleBookClick(book.id)}
            >
              <Card.Body>
                <Card.Title>{book.bookTitle}</Card.Title>
                <Card.Text>
                  <strong>Author:</strong> {book.author}
                </Card.Text>
                <Card.Text>
                  <strong>Price:</strong> {book.bookPrice}
                </Card.Text>
                <Card.Text>
                  <strong>ISBN:</strong> {book.isbn}
                </Card.Text>
                <Button variant="primary" className="mb-2">ADD</Button>
                <Button variant="warning" className="ml-2 mb-2">UPDATE</Button>
              </Card.Body>
            </Card>
          ))}
        </div>
      )}
    </div>
   
  );
}

export default AdminDashboard;





































