import React, { useState } from "react";
import { Button, Form, Nav, NavDropdown, Navbar } from "react-bootstrap";
import { useNavigate } from "react-router";

function Addbook(){


    const [formData, setFormData] = useState({
        bookTitle: "",
        category: "",
        author: "",
        rating: "",
        isbn: "",
        bookDesc: "",
        authorDesc: "",
        bookPrice: "",
        noOfCopies: "",
      });
    
      const navigate = useNavigate();
      const [msg, setMsg] = useState("");
    
      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
          ...prevData,
          [name]: value,
        }));
      };
    
      const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        setFormData({
          bookTitle: "",
          category: "",
          author: "",
          rating: "",
          isbn: "",
          bookDesc: "",
          authorDesc: "",
          bookPrice: "",
          noOfCopies: "",
        });
      };


return(
<div>
<div>
      <Navbar bg="dark" data-bs-theme="dark" style={{ padding: 10 }}>
        <Navbar.Brand ><h2>INFOLIBRA</h2></Navbar.Brand>
        <Nav className="me-auto  mr-4">
          <Nav.Link >Admin Dashboard</Nav.Link>
          <Nav.Link >Orders</Nav.Link>
        </Nav>
        <Navbar.Collapse className="justify-content-end">
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          {
            localStorage.getItem('isLoggedIn') ?
              <React.Fragment>
                <Navbar.Text >
                  Signed in as: <span style={{ color: "white" }}>
                    {localStorage.getItem('username')}
                  </span>
                </Navbar.Text>
                &nbsp;&nbsp;&nbsp;
                <button className="btn btn-info btn-sm ml-4" onClick={() => {
                  localStorage.clear();
                  navigate('/auth/login?msg=you have logged out..')
                }}>Logout</button>
              </React.Fragment>
              :
              <button className="btn btn-primary" onClick={() => navigate('/auth/login')}>Login</button>
          }
        </Navbar.Collapse>
      </Navbar>
      <NavDropdown title="Options" id="basic-nav-dropdown" style={customStyle}>
        <NavDropdown.Item onClick={AddBook}>Add</NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Delete clicked")}>
          Delete
        </NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Update clicked")}>
          Update
        </NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Status clicked")}>
          Status
        </NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item
          onClick={() => {
            localStorage.clear();
            navigate("/auth/login?msg=you have logged out..");
          }}
        >
          Logout
        </NavDropdown.Item>
      </NavDropdown>



      <div>
      <Form onSubmit={handleSubmit}>
  <Form.Group controlId="formBookTitle">
    <Form.Label>Book Title</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter book title"
      name="bookTitle"
      value={formData.bookTitle}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formAuthor">
    <Form.Label>Author</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter author"
      name="author"
      value={formData.author}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formRating">
    <Form.Label>Rating</Form.Label>
    <Form.Control
      type="number"
      placeholder="Enter rating"
      name="rating"
      value={formData.rating}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formCategory">
    <Form.Label>Category</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter category"
      name="category"
      value={formData.category}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formNoOfCopies">
    <Form.Label>Number of Copies</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter number of copies"
      name="noOfCopies"
      value={formData.noOfCopies}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formIsbn">
    <Form.Label>ISBN</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter ISBN"
      name="isbn"
      value={formData.isbn}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formBookDesc">
    <Form.Label>Book Description</Form.Label>
    <Form.Control
      as="textarea"
      rows={3}
      placeholder="Enter book description"
      name="bookDesc"
      value={formData.bookDesc}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formAuthorDesc">
    <Form.Label>Author Description</Form.Label>
    <Form.Control
      as="textarea"
      rows={3}
      placeholder="Enter author description"
      name="authorDesc"
      value={formData.authorDesc}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formBookPrice">
    <Form.Label>Book Price</Form.Label>
    <Form.Control
      type="number"
      placeholder="Enter book price"
      name="bookPrice"
      value={formData.bookPrice}
      onChange={handleChange}
    />
  </Form.Group>

  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>

      </div>
    </div>





    
</div>



);

}
export default Addbook;
