import React, { useEffect, useState } from "react";
import Navbarcomponent from "../navbar";
import axios from "axios";
import { Table } from "react-bootstrap";
import { useParams } from "react-router";

function BookingStatus() {
  const [customerBook, setCustomerBook] = useState([]);
  const { id } = useParams();
  const [cartBooks, setCartBooks] = useState([]);

  useEffect(() => {
    // Fetch customer books from the API
    axios
      .get(`http://localhost:8182/customerBook/customerid/${id}`)
      .then((response) => {
        console.log("API response:", response.data);
        setCustomerBook(response.data);
      })
      .catch((error) =>
        console.error("Error fetching customer books:", error)
      );

    // Fetch cart books from your cart state (assuming it contains book IDs)
    // You need to replace 'cart' with your actual cart state or prop
    const cartBookIds = cart.map((book) => book.id);
    setCartBooks(cartBookIds);
  }, [id, cart]); // Include 'cart' in the dependency array

  const calculateTotalCost = () => {
    return customerBook.reduce((total, book) => total + book.amount, 0);
  };

  // Filter customer books based on cartBookIds
  const filteredBooks = customerBook.filter((book) =>
    cartBooks.includes(book.id)
  );

  return (
    <div>
      <Navbarcomponent />
      <h1>Booking Status</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Id</th>
            <th>Issue Date</th>
            <th>Return Date</th>
            <th>Fine</th>
          </tr>
        </thead>
        <tbody>
          {filteredBooks.map((cb) => (
            <tr key={cb.id}>
              <td>{cb.id}</td>
              <td>{cb.issueDate}</td>
              <td>{cb.returnDate}</td>
              <td>{cb.amount}</td>
            </tr>
          ))}
        </tbody>
      </Table>
      <div>
        <p>Total Number of Books: {filteredBooks.length}</p>
        <p>Total Cost: {calculateTotalCost()}</p>
      </div>
    </div>
  );
}

export default BookingStatus;
