// CartComponent.js
import React from "react";
import CartItem from "./CartItem";

function CartComponent({ cart, setCart }) {
  const removeFromCart = (removedBook) => {
    const updatedCart = cart.filter((book) => book.id !== removedBook.id);
    setCart(updatedCart);
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      {cart.map((book) => (
        <CartItem key={book.id} book={book} removeFromCart={removeFromCart} />
      ))}
    </div>
  );
}

export default CartComponent;
