import React, { useState } from "react";
import axios from "axios";
import { Card } from "react-bootstrap";

function AdminDashboard() {
  const [books, setBooks] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [customerBookings, setCustomerBookings] = useState([]);
  const [showBooks, setShowBooks] = useState(false);
  const [showCustomers, setShowCustomers] = useState(false);
  const [showCustomerBookings, setShowCustomerBookings] = useState(false);
  const [selectedBookId, setSelectedBookId] = useState(null);
  const [customersByBookId, setCustomersByBookId] = useState([]);
  const [showCustomersByBookId, setShowCustomersByBookId] = useState(false);

  const fetchBooks = async () => {
    try {
      const response = await axios.get("http://localhost:8182/Book/all");
      setBooks(response.data);
      setShowBooks(true);
    } catch (error) {
      console.error("Error fetching books:", error.message);
    }
  };

  const hideBooks = () => {
    setBooks([]);
    setShowBooks(false);
  };

  const fetchCustomers = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customer/getall");
      setCustomers(response.data);
      setShowCustomers(true);
    } catch (error) {
      console.error("Error fetching customers:", error.message);
    }
  };

  const hideCustomers = () => {
    setCustomers([]);
    setShowCustomers(false);
  };

  const fetchCustomerBookings = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customerBook/getall");
      setCustomerBookings(response.data);
      setShowCustomerBookings(true);
    } catch (error) {
      console.error("Error fetching customer bookings:", error.message);
    }
  };

  const hideCustomerBookings = () => {
    setCustomerBookings([]);
    setShowCustomerBookings(false);
  };

  const fetchCustomersByBookId = async (bookId) => {
    try {
      const response = await axios.get(`http://localhost:8182/customerBook/customerid/${bookId}`);
      setCustomersByBookId(response.data);
      setShowCustomersByBookId(true);
    } catch (error) {
      console.error("Error fetching customers by book id:", error.message);
    }
  };

  const hideCustomersByBookId = () => {
    setCustomersByBookId([]);
    setShowCustomersByBookId(false);
  };

  const handleBookClick = (bookId) => {
    setSelectedBookId(bookId);
    fetchCustomersByBookId(bookId);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Admin Dashboard</h1>

      <button
        className={`btn ${showBooks ? "btn-danger" : "btn-success"} mb-2`}
        onClick={showBooks ? hideBooks : fetchBooks}
      >
        {showBooks ? `Hide Books (${books.length})` : `Show Books (${books.length})`}
      </button>

      {showBooks && (
        <div className="d-flex flex-wrap">
          {books.map((book) => (
            <Card
              key={book.id}
              className="m-2"
              style={{ width: "18rem", cursor: "pointer" }}
              onClick={() => handleBookClick(book.id)}
            >
              <Card.Body>
                <Card.Title>{book.bookTitle}</Card.Title>
                <Card.Text>
                  <strong>Author:</strong> {book.author}
                </Card.Text>
                <Card.Text>
                  <strong>Price:</strong> {book.bookPrice}
                </Card.Text>
                <Card.Text>
                  <strong>ISBN:</strong> {book.isbn}
                </Card.Text>
              </Card.Body>
            </Card>
          ))}
        </div>
      )}

      <button
        className={`btn ${showCustomers ? "btn-danger" : "btn-success"} mb-2 ml-2`}
        onClick={showCustomers ? hideCustomers : fetchCustomers}
      >
        {showCustomers ? `Hide Customers (${customers.length})` : `Show Customers (${customers.length})`}
      </button>

      {showCustomers && (
        <div className="d-flex flex-wrap">
          {customers.map((customer) => (
            <Card key={customer.id} className="m-2" style={{ width: "18rem" }}>
              <Card.Body>
                <Card.Title>{customer.name}</Card.Title>
                <Card.Text>
                  <strong>City:</strong> {customer.city}
                </Card.Text>
                <Card.Text>
                  <strong>Contact:</strong> {customer.contact}
                </Card.Text>
              </Card.Body>
            </Card>
          ))}
        </div>
      )}

      <button
        className={`btn ${showCustomerBookings ? "btn-danger" : "btn-success"} mb-2 ml-2`}
        onClick={showCustomerBookings ? hideCustomerBookings : fetchCustomerBookings}
      >
        {showCustomerBookings ? `Hide Customer Bookings (${customerBookings.length})` : `Show Customer Bookings (${customerBookings.length})`}
      </button>

      {showCustomerBookings && (
        <div className="d-flex flex-wrap">
          {customerBookings.map((booking) => (
            <Card key={booking.id} className="m-2" style={{ width: "18rem" }}>
              <Card.Body>
                <Card.Title>{booking.customer.name}</Card.Title>
                <Card.Text>
                  <strong>Issue Date:</strong> {booking.issueDate}
                </Card.Text>
                <Card.Text>
                  <strong>Return Date:</strong> {booking.returnDate}
                </Card.Text>
                <Card.Text>
                  <strong>Fine Amount:</strong> {booking.amount}
                </Card.Text>
              </Card.Body>
            </Card>
          ))}
        </div>
      )}

      {selectedBookId && (
        <div>
          <h3>Customers who purchased this book:</h3>
          <button
            className={`btn ${showCustomersByBookId ? "btn-danger" : "btn-success"} mb-2 ml-2`}
            onClick={showCustomersByBookId ? hideCustomersByBookId : () => fetchCustomersByBookId(selectedBookId)}
          >
            {showCustomersByBookId ? `Hide Customers (${customersByBookId.length})` : `Show Customers (${customersByBookId.length})`}
          </button>

          {showCustomersByBookId && (
            <div className="d-flex flex-wrap">
              {customersByBookId.map((customer) => (
                <Card key={customer.id} className="m-2" style={{ width: "18rem" }}>
                  <Card.Body>
                    <Card.Title>{customer.name}</Card.Title>
                    <Card.Text>
                      <strong>City:</strong> {customer.city}
                    </Card.Text>
                    <Card.Text>
                      <strong>Contact:</strong> {customer.contact}
                    </Card.Text>
                  </Card.Body>
                </Card>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;







































