// CartComponent.js

import React from 'react';
import { Card, Row, Button } from 'react-bootstrap';
import { Link, Navigate,  } from 'react-router-dom';

function CartComponent({ cart, setCart }) {
  const handleButtonClick = () => {
    // Navigate to the booking page
    Navigate("/booking");
  };
  return (
    <div>
      <h2>Cart</h2>
      <div className="container col-md-8">
        <Row>
          {cart.map((b, index) => (
            <div key={index} className="col-md-4 mb-4">
              <Card style={{ width: '400px', backgroundColor: '#your_card_background_color_here' }}>
                <Card.Body style={{ backgroundColor: '#your_card_body_background_color_here' }}>
                  <Link to={'/books?id='+b.id} >
                    <Card.Title>
                      {b.bookTitle}
                    </Card.Title>
                  </Link>
                  <hr />
                  <div>
                    <div style={{ display: 'flex', flexDirection: 'column', marginBottom: '10px' }}>
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Rating:</span> {b.rating}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Author:</span>
                        <Link to={'/authors?id='+b.id}>
                          {b.author}
                        </Link>
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Category:</span> {b.category.name}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Price:</span> {b.bookPrice}
                      </div>
                    </div>
                    <Button onClick={() => setCart(prevCart => prevCart.filter(item => item.id !== b.id))}>Remove from Cart</Button>
                  </div>
                </Card.Body>
              </Card>
            </div>
          ))}
          <div class="page-content page-container" id="page-content">
            <div class="padding">
              <div class="row container d-flex justify-content-center">
                <button type="button"  class="btn btn-success btn-icon-text bouncebutton"> <i class="fa fa-check btn-icon-prepend"></i>BOOK NOW</button>
              </div>
            </div>
          </div>
        </Row>
      </div>
    </div>
  );
}

export default CartComponent;
