import { Col, Form, Nav, NavDropdown, Navbar, Row } from "react-bootstrap";
import { Button } from "react-bootstrap";
import React, { useState } from "react";
import { useNavigate } from "react-router";
import axios from "axios";

function AdminDashboard() {
  const [bookTitle, setBookTitle] = useState('');
  const [category, setCategory] = useState('');
  const [author, setAuthor] = useState('');
  const [rating, setRating] = useState('');
  const [isbn, setIsbn] = useState('');
  const [bookDesc, setBookDesc] = useState('');
  const [authorDesc, setAuthorDesc] = useState('');
  const [bookPrice, setBookPrice] = useState('');
  const [noOfCopies, setNoOfCopies] = useState('');
  const [catid, setCatid] = useState(/* initial value */);
  const [id, setId] = useState(/* initial value */);

  const [book, setBook] = useState({});
  const navigate = useNavigate();
  const [msg, setMsg] = useState('');
  

//   const AddBook = (e) => {
//     e.preventDefault();

//     let categoryObj = {
//       "category":category,
//       "bookTitle": bookTitle,
//       "author": author,
//       "rating": rating,
//       "isbn": isbn,
//       "bookPrice": bookPrice,
//       "noOfCopies": noOfCopies
//     };

//     try {
     
//       const response = axios.post(`http://localhost:8182/Book/add/${id}`, categoryObj);
      
//       // Log the entire response
//       console.log(response);

//       const responseData = response.data;
     
//       setId(id);

     
//   } catch (error) {
//       setMsg('nothing to show');
//   }
// };
  return (
    <div>
      <Navbar bg="dark" data-bs-theme="dark" style={{ padding: 10 }}>
        <Navbar.Brand ><h2>INFOLIBRA</h2></Navbar.Brand>
        <Nav className="me-auto  mr-4">
          <Nav.Link >Admin Dashboard</Nav.Link>
          <Nav.Link >Orders</Nav.Link>
        </Nav>
        <Navbar.Collapse className="justify-content-end">
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          {
            localStorage.getItem('isLoggedIn') ?
              <React.Fragment>
                <Navbar.Text >
                  Signed in as: <span style={{ color: "white" }}>
                    {localStorage.getItem('username')}
                  </span>
                </Navbar.Text>
                &nbsp;&nbsp;&nbsp;
                <button className="btn btn-info btn-sm ml-4" onClick={() => {
                  localStorage.clear();
                  navigate('/auth/login?msg=you have logged out..')
                }}>Logout</button>
              </React.Fragment>
              :
              <button className="btn btn-primary" onClick={() => navigate('/auth/login')}>Login</button>
          }
        </Navbar.Collapse>
      </Navbar>
      <NavDropdown title="Options" id="basic-nav-dropdown" style={customStyle}>
        {/* <NavDropdown.Item onClick={AddBook}>Add</NavDropdown.Item> */}
        <NavDropdown.Item onClick={() => console.log("Delete clicked")}>
          Delete
        </NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Update clicked")}>
          Update
        </NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Status clicked")}>
          Status
        </NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item
          onClick={() => {
            localStorage.clear();
            navigate("/auth/login?msg=you have logged out..");
          }}
        >
          Logout
        </NavDropdown.Item>
      </NavDropdown>



      <div>
      <Form onSubmit={handleSubmit}>
  <Form.Group controlId="formBookTitle">
    <Form.Label>Book Title</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter book title"
      name="bookTitle"
      value={formData.bookTitle}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formAuthor">
    <Form.Label>Author</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter author"
      name="author"
      value={formData.author}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formRating">
    <Form.Label>Rating</Form.Label>
    <Form.Control
      type="number"
      placeholder="Enter rating"
      name="rating"
      value={formData.rating}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formCategory">
    <Form.Label>Category</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter category"
      name="category"
      value={formData.category}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formNoOfCopies">
    <Form.Label>Number of Copies</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter number of copies"
      name="noOfCopies"
      value={formData.noOfCopies}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formIsbn">
    <Form.Label>ISBN</Form.Label>
    <Form.Control
      type="text"
      placeholder="Enter ISBN"
      name="isbn"
      value={formData.isbn}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formBookDesc">
    <Form.Label>Book Description</Form.Label>
    <Form.Control
      as="textarea"
      rows={3}
      placeholder="Enter book description"
      name="bookDesc"
      value={formData.bookDesc}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formAuthorDesc">
    <Form.Label>Author Description</Form.Label>
    <Form.Control
      as="textarea"
      rows={3}
      placeholder="Enter author description"
      name="authorDesc"
      value={formData.authorDesc}
      onChange={handleChange}
    />
  </Form.Group>

  <Form.Group controlId="formBookPrice">
    <Form.Label>Book Price</Form.Label>
    <Form.Control
      type="number"
      placeholder="Enter book price"
      name="bookPrice"
      value={formData.bookPrice}
      onChange={handleChange}
    />
  </Form.Group>

  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>

      </div>
    </div>
    

    


  );
}

export default AdminDashboard;
