// ... (your imports)

import { Button, Card, Row } from "react-bootstrap";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";

function CartComponent({ cart, setCart, customerId }) {
  const navigate = useNavigate();

  const handleButtonClick = () => {
    // Navigate to the booking page
    navigate("/booking");
  };

  const handleIncrement = (id) => {
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  const handleDecrement = (id) => {
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === id && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  const handleRemoveFromCart = (id) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== id));
  };

  const handleBookAll = () => {
    // Call the API to book all books for the customer with the given ID
    // You can use the customerId in the API call
    // After booking, you may want to clear the cart or update its state
    alert("Book All functionality to be implemented");
  };

  return (
    <div>
      <br />
      <br />
      <br />
      <br />
      <h2>Cart</h2>
      <br />
      <br />
      <br />
      <br />
      <div className="container col-md-8">
        <Row>
          {cart.map((b, index) => (
            <div key={index} className="col-md-4 mb-4">
              <Card
                style={{
                  width: '400px',
                  backgroundColor: '#your_card_background_color_here',
                }}
              >
                <Card.Body
                  style={{
                    backgroundColor: '#your_card_body_background_color_here',
                  }}
                >
                  <Link to={'/books?id=' + b.id}>
                    <Card.Title>{b.bookTitle}</Card.Title>
                  </Link>
                  <hr />
                  <div>
                    <div
                      style={{
                        display: 'flex',
                        flexDirection: 'column',
                        marginBottom: '10px',
                      }}
                    >
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Rating:</span>{' '}
                        {b.rating}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Author:</span>
                        <Link to={'/authors?id=' + b.id}>{b.author}</Link>
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Category:</span>{' '}
                        {b.category.name}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold' }}>Price:</span>{' '}
                        {b.bookPrice}
                      </div>
                      <div>
                        <Button onClick={() => handleDecrement(b.id)}>
                          -
                        </Button>{' '}
                        &nbsp;
                        <Button onClick={() => handleRemoveFromCart(b.id)}>
                          Remove from Cart
                        </Button>
                        <span style={{ margin: '0 5px' }}>{b.quantity}</span>
                        <Button onClick={() => handleIncrement(b.id)}>
                          +
                        </Button>
                      </div>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            </div>
          ))}
          <div class="page-content page-container" id="page-content">
            <div class="padding">
              <div class="row container d-flex justify-content-center">
                <Button
                  variant="success"
                  onClick={handleBookAll}
                >
                  Book All
                </Button>
                &nbsp;&nbsp;
                <button
                  type="button"
                  onClick={handleButtonClick}
                  class="btn btn-success btn-icon-text bouncebutton"
                  style={{ fontSize: '30px' }}
                >
                  <i class="fa fa-check btn-icon-prepend"></i>Proceed to Buy
                </button>
              </div>
            </div>
          </div>
        </Row>
      </div>
    </div>
  );
}

export default CartComponent;
