import React, { useEffect, useState } from "react";
import Navbarcomponent from "../navbar";
import { Button, NavDropdown, Form } from "react-bootstrap"; 
import axios from "axios";
import { useParams } from "react-router";
import AdminNavbar from "./navbar";

function AddingBook() {
  const { id } = useParams();
 
  const [book, setBook] = useState({})
  
  const [bookTitle, setBookTitle] = useState('');
  const [category, setCategory] = useState('');
  const [author, setAuthor] = useState('');
  const [rating, setRating] = useState('');
  const [isbn, setIsbn] = useState('');
  const [bookDesc, setBookDesc] = useState('');
  const [authorDesc, setAuthorDesc] = useState('');
  const [bookPrice, setBookPrice] = useState('');
  const [noOfCopies, setNoOfCopies] = useState('');
  const isUpdateMode = !!id;

  useEffect(() => {
    if (isUpdateMode) {
      // Fetch book data by id and set it to state
      axios.get(`http://localhost:8182/Book/${id}`)
        .then(response => setBook(response.data))
        .catch(error => console.error("Error fetching book:", error));
    }
  }, [id, isUpdateMode]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = {
      "bookTitle": bookTitle,
      "category": category,
      "author": author,
      "rating": rating,
      "isbn": isbn,
      "bookDesc": bookDesc,
      "authorDesc": authorDesc,
      "bookPrice": bookPrice,
      "noOfCopies": noOfCopies,
    };

    const apiUrl = isUpdateMode
      ? `http://localhost:8182/Book/update/${id}`
      : `http://localhost:8182/Book/add/${id}`;

    axios.post(apiUrl, data)
      .then(response => setBook(response.data))
      .catch(error => console.error("Error:", error));
  };

  return (<div>
    <AdminNavbar />
    {console.log(id)}
    
    <div>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formBookTitle">
          <Form.Label><h5>Book Title</h5></Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter book title"
            name="bookTitle"

            onChange={(e) => setBookTitle(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formAuthor">
          <Form.Label><h5>Author</h5></Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter author"
            name="author"

            onChange={(e) => setAuthor(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formRating">
          <Form.Label><h5>Rating</h5></Form.Label>
          <Form.Control
            type="float"
            placeholder="Enter rating"
            name="rating"

            onChange={(e) => setRating(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formCategory">
          <Form.Label><h5>Category</h5></Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter category"
            name="category"

            onChange={(e) => setCategory(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formNoOfCopies">
          <Form.Label><h5>Number of Copies</h5></Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter number of copies"
            name="noOfCopies"

            onChange={(e) => setNoOfCopies(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formIsbn">
          <Form.Label><h5>ISBN</h5></Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter ISBN"
            name="isbn"

            onChange={(e) => setIsbn(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formBookDesc">
          <Form.Label><h5>Book Description</h5></Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            placeholder="Enter book description"
            name="bookDesc"

            onChange={(e) => setBookDesc(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formAuthorDesc">
          <Form.Label><h5>Author Description</h5></Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            placeholder="Enter author description"
            name="authorDesc"

            onChange={(e) => setAuthorDesc(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="formBookPrice">
          <Form.Label><h5>Book Price</h5></Form.Label>
          <Form.Control
            type="number"
            placeholder="Enter book price"
            name="bookPrice"

            onChange={(e) => setBookPrice(e.target.value)}
          />
        </Form.Group>
        <br />
        <Button variant="primary" type="submit">
            {isUpdateMode ? "Update" : "Submit"}
          </Button>
          {!isUpdateMode && (
            <Button variant="primary" type="reset">
              Reset
            </Button>
          )}
      </Form>
    </div>
  </div>
  );
}
export default AddingBook;
