// Navbarcomponent.js
import React from 'react';
import { Nav, Navbar } from 'react-bootstrap';
import { Link, useNavigate, useParams } from 'react-router-dom';

function Navbarcomponent() {
  const { id } = useParams();
  const navigate = useNavigate();

  const handlePreviousOrdersClick = () => {
    navigate(`/previous_orders/${id}`);
  };

  const handleBooks = () => {
    if (localStorage.getItem('isLoggedIn')) {
      navigate(`/book/cart/${id}`);
    } else {
      navigate('/auth/login');
    }
  };

  const handleChatWithUsClick = () => {
    // Open WhatsApp for chat (replace with your WhatsApp number)
    window.location.href = 'https://wa.me/+918341467698'; // Replace with your WhatsApp number
  };

  return (
    <div>
      <Navbar bg="dark" variant="dark" className="justify-conent-between" fixed="top" style={{ padding: 10, marginBottom: 0 }}>
        <Navbar.Brand>
          <h2 style={{ margin: 0 }}>INFOLIBRA</h2>
        </Navbar.Brand>
        <Nav className="me-auto mr-4">
          <Nav.Link onClick={handlePreviousOrdersClick}>Previous orders</Nav.Link>
          <Nav.Link onClick={handleChatWithUsClick}>Chat with Us</Nav.Link>
        </Nav>
        <Nav className="me-auto mr-4">
          <Nav.Link onClick={handleBooks}>Cart</Nav.Link>
        </Nav>
        <Navbar.Collapse className="justify-content-end">
          {localStorage.getItem('isLoggedIn') ? (
            <>
            
              <Navbar.Text>
                Signed in as: <span style={{ color: 'white' }}>{localStorage.getItem('username')}</span>
              </Navbar.Text>
              &nbsp;&nbsp;&nbsp;
              <Nav.Link as={Link} to="/about">
  About
</Nav.Link>
              <button
                className="btn btn-info btn-sm ml-4"
                onClick={() => {
                  localStorage.clear();
                  navigate('/auth/login?msg=you have logged out..');
                }}
              >
                Logout
              </button>
            </>
          ) : (
            <button className="btn btn-primary" onClick={() => navigate('/auth/login')}>
              Login
            </button>
          )}
        </Navbar.Collapse>
      </Navbar>
    </div>
  );
}

export default Navbarcomponent;
