import React, { Component } from "react";
import axios from "axios";
import { Card, Button } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import AdminNavbar from "./adminnavbar";

class AdminDashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      customers: [],
      customerBookings: [],
      showBooks: false,
      showCustomers: false,
      showCustomerBookings: false,
    };
  }

  componentDidMount() {
    this.fetchBooks();
    this.fetchCustomers();
    this.fetchCustomerBookings();
  }

  fetchBooks = async () => {
    try {
      const response = await axios.get("http://localhost:8182/Book/all");
      this.setState({ books: response.data });
    } catch (error) {
      console.error("Error fetching books:", error.message);
    }
  };

  fetchCustomers = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customer/getall");
      this.setState({ customers: response.data });
    } catch (error) {
      console.error("Error fetching customers:", error.message);
    }
  };

  fetchCustomerBookings = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customerBook/getall");
      this.setState({ customerBookings: response.data });
    } catch (error) {
      console.error("Error fetching customer bookings:", error.message);
    }
  };

  navigateToAddBook = () => {
    const { history, match } = this.props;
    history.push(`/admin/add/${match.params.id}`);
  };

  navigateToUpdateBook = (bookId) => {
    const { history } = this.props;
    history.push(`/admin/update/${bookId}`);
  };

  handleDeleteBook = async (bookId) => {
    try {
      await axios.delete(`http://localhost:8182/Book/delete/${bookId}`);
      this.fetchBooks();
    } catch (error) {
      console.error("Error deleting book:", error.message);
    }
  };

  renderUniqueBooks = (books) => {
    const uniqueBooks = [];
    const bookIds = new Set();

    for (const book of books) {
      if (!bookIds.has(book.id)) {
        uniqueBooks.push(book);
        bookIds.add(book.id);
      }
    }

    return uniqueBooks;
  };

  renderUniqueCustomers = (customers) => {
    const uniqueCustomers = [];
    const customerIds = new Set();

    for (const customer of customers) {
      if (!customerIds.has(customer.id)) {
        uniqueCustomers.push(customer);
        customerIds.add(customer.id);
      }
    }

    return uniqueCustomers;
  };

  renderUniqueCustomerBookings = (customerBookings) => {
    const uniqueCustomerBookings = [];
    const bookingIds = new Set();

    for (const booking of customerBookings) {
      if (!bookingIds.has(booking.id)) {
        uniqueCustomerBookings.push(booking);
        bookingIds.add(booking.id);
      }
    }

    return uniqueCustomerBookings;
  };

  render() {
    const { books, customers, customerBookings, showBooks, showCustomers, showCustomerBookings } = this.state;

    return (
      <div>
        <AdminNavbar />

        <div className="container mt-5">
          <h1 className="mb-4">Admin Dashboard</h1>

          <button
            className={`btn ${showBooks ? "btn-danger" : "btn-success"} mb-2`}
            onClick={() => this.setState({ showBooks: !showBooks })}
          >
            {showBooks ? `Hide Books (${books.length})` : `Show Books (${books.length})`}
          </button>

          {showBooks && (
            <div className="d-flex flex-wrap">
              {books.map((book) => (
                <Card key={book.id} className="m-2" style={{ width: "18rem" }}>
                  <Card.Body>
                    <Card.Title>{book.bookTitle}</Card.Title>
                    <Card.Text>
                      <strong>Author:</strong> {book.author}
                    </Card.Text>
                    <Card.Text>
                      <strong>Price:</strong> {book.bookPrice}
                    </Card.Text>
                    <Card.Text>
                      <strong>ISBN:</strong> {book.isbn}
                    </Card.Text>
                    <Button
                      variant="primary"
                      className="mr-2"
                      onClick={() => this.navigateToAddBook()}
                    >
                      ADD
                    </Button>{" "}
                    <Button
                      variant="info"
                      onClick={() => this.navigateToUpdateBook(book.id)}
                    >
                      UPDATE
                    </Button>{" "}
                    <Button
                      variant="danger"
                      onClick={() => this.handleDeleteBook(book.id)}
                    >
                      DELETE
                    </Button>
                  </Card.Body>
                </Card>
              ))}
            </div>
          )}

          &nbsp;&nbsp;&nbsp;&nbsp;

          <button
            className={`btn ${showCustomers ? "btn-danger" : "btn-success"} mb-2 ml-2`}
            onClick={() => this.setState({ showCustomers: !showCustomers })}
          >
            {showCustomers ? `Hide Customers (${customers.length})` : `Show Customers (${customers.length})`}
          </button>

          {showCustomers && (
            <div className="d-flex flex-wrap">
              {customers.map((customer) => (
                <Card key={customer.id} className="m-2" style={{ width: "18rem" }}>
                  <Card.Body>
                    <Card.Title>{customer.name}</Card.Title>
                    <Card.Text>
                      <strong>City:</strong> {customer.city}
                    </Card.Text>
                    <Card.Text>
                      <strong>Contact:</strong> {customer.contact}
                    </Card.Text>
                  </Card.Body>
                </Card>
              ))}
            </div>
          )}

          &nbsp;&nbsp;&nbsp;&nbsp;

          <button
            className={`btn ${showCustomerBookings ? "btn-danger" : "btn-success"} mb-2 ml-2`}
            onClick={() => this.setState({ showCustomerBookings: !showCustomerBookings })}
          >
            {showCustomerBookings ? "Hide Customer Bookings" : "Show Customer Bookings"}
          </button>

          {showCustomerBookings && (
            <div className="d-flex flex-wrap">
              {this.renderUniqueCustomerBookings(customerBookings).map((booking) => (
                <Card key={booking.id} className="m-2" style={{ width: "18rem" }}>
                  <Card.Body>
                    <Card.Title>{booking.customer ? booking.customer.name : ""}</Card.Title>
                    <Card.Text>
                      <strong>Issue Date:</strong> {booking.issueDate}
                    </Card.Text>
                    <Card.Text>
                      <strong>Return Date:</strong> {booking.returnDate}
                    </Card.Text>
                    <Card.Text>
                      <strong>Fine Amount:</strong> {booking.amount}
                    </Card.Text>
                    <Card.Text>
                      <strong>Book id:</strong> {booking.id}
                    </Card.Text>
                  </Card.Body>
                </Card>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default withRouter(AdminDashboard);
