// components/Auth/login/Login.js

import React, { useState } from "react";
import { Col, Button, Row, Container, Card, Form } from "react-bootstrap";
import './login.css'; // Import the CSS file
import { useSearchParams } from "react-router-dom";

function Login() {
  const [param] = useSearchParams();
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };
  const handleLogin = async (event) => {
    event.preventDefault();

    // Assuming your Spring Boot backend is running on http://localhost:8182
    const apiUrl = "http://localhost:8182/user/login";
    const formData = new FormData(event.target);

    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        // Handle successful login
        console.log("Login successful");
      } else {
        // Handle login error
        console.error("Login failed");
      }
    } catch (error) {
      // Handle network error
      console.error("Network error", error);
    }
  };

  return (
      <div className="container mt-4">
         
          <div className="row">
            <div className="col-md-3"></div>
            <div className="col-md-6">
              <div className="card">
                <div className="card-header">
                    <h3>Login</h3>
                </div>
                <div className="card-body">
                    {msg !==  null?
                          <div className="alert alert-danger" role="alert">
                            {msg}
                        </div>
                    :''}    
                  <div className="row " style={{textAlign: "right"}}>
                    <div className="col-md-6">
                      <label>Enter Email/Username:</label>
                    </div>
                    <div className="col-md-6 mb-4">
                      <input type="email" className="form-control" 
                      onChange={(e)=>setUsername(e.target.value)}/>
                    </div>
                  </div>
                  <div className="row" style={{textAlign: "right"}}>
                    <div className="col-md-6">
                      <label>Enter Password:</label>
                    </div>
                    <div className="col-md-6">
                      <input type="password" className="form-control" 
                      onChange={(e)=>setPassword(e.target.value)} />
                    </div>
                  </div>
                   
                </div>
                <div className="card-footer" style={{textAlign: "right"}}>
                    <button className="btn btn-primary" 
                    onClick={()=>doLogin()}>Login</button>
                  </div>
              </div>
              <div style={{textAlign: "left"}} className="mt-4">
                  <span>Don't have an Account 
                    <button className="button_link" 
                        onClick={()=>navigate('/auth/signup')}>Sign Up</button>
                    </span>
              </div>
            </div>
            <div className="col-md-3"></div>
          </div>
        
      </div>
    ); 
}

export default Login;
