import React, { useState } from "react";
import axios from "axios";
import { Card } from "react-bootstrap";

function AdminDashboard() {
  const [books, setBooks] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [showBooks, setShowBooks] = useState(false);
  const [showCustomers, setShowCustomers] = useState(false);

  const fetchBooks = async () => {
    try {
      const response = await axios.get("http://localhost:8182/Book/all");
      setBooks(response.data);
      setShowBooks(true);
    } catch (error) {
      console.error("Error fetching books:", error.message);
    }
  };

  const hideBooks = () => {
    setBooks([]);
    setShowBooks(false);
  };

  const fetchCustomers = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customer/getall");
      setCustomers(response.data);
      setShowCustomers(true);
    } catch (error) {
      console.error("Error fetching customers:", error.message);
    }
  };

  const hideCustomers = () => {
    setCustomers([]);
    setShowCustomers(false);
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <button onClick={showBooks ? hideBooks : fetchBooks}>
        {showBooks ? "Hide Books" : "Show Books"}
      </button>

      {showBooks && (
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          {books.map((book) => (
            <Card key={book.id} style={{ width: "18rem", margin: "10px" }}>
              <Card.Body>
                <Card.Title>{book.bookTitle}</Card.Title>
                <Card.Text>
                  <strong>Author:</strong> {book.author}
                </Card.Text>
                <Card.Text>
                  <strong>Price:</strong> {book.bookPrice}
                </Card.Text>
                <Card.Text>
                  <strong>ISBN:</strong> {book.isbn}
                </Card.Text>
                {/* Add more details as needed */}
              </Card.Body>
            </Card>
          ))}
        </div>
      )}

      <button onClick={showCustomers ? hideCustomers : fetchCustomers}>
        {showCustomers ? "Hide Customers" : "Show Customers"}
      </button>

      {showCustomers && (
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          {customers.map((customer) => (
            <Card key={customer.id} style={{ width: "18rem", margin: "10px" }}>
              <Card.Body>
                <Card.Title>{customer.name}</Card.Title>
                <Card.Text>
                  <strong>City:</strong> {customer.city}
                </Card.Text>
                <Card.Text>
                  <strong>Contact:</strong> {customer.contact}
                </Card.Text>
                {/* Add more details as needed */}
              </Card.Body>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;


















