// BookDetails.js
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function BookDetails() {
  const { bookId } = useParams();
  const [bookDetails, setBookDetails] = useState(null);

  useEffect(() => {
    // Fetch book details based on bookId
    axios.get(`http://localhost:8182/Book/details/${bookId}`)
      .then(response => setBookDetails(response.data))
      .catch(error => console.error("Error fetching book details:", error));
  }, [bookId]);

    
  axios.get(`http://localhost:8182//getone/${bookId}`)
  .then(response => setBookDetails(response.data))

  if (!bookDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{bookDetails.bookTitle}</h2>
      <p>{bookDetails.bookDescription}</p>
      {/* Add more details as needed */}
    </div>
  );
}

export default BookDetails;
