import React from 'react';
import { Card, Button } from 'react-bootstrap';
import Navbarcomponent from '../navbar';
import { useNavigate } from 'react-router';

function BookingComponent({ cart, setCart }) {
  const totalPrice = cart.reduce((sum, book) => sum + book.bookPrice, 0);
  const totalBooks = cart.length;
  const navigate = useNavigate();

  const handleButtonClick = () => {
    // Navigate to the booking page
    navigate("/booking");
  };

  return (
    <div>
      <div>
        <Navbarcomponent />
      </div>

      <div>
        <h1>Booking Info...</h1>
        {cart.map((b, index) => (
          <div key={index} className="col-md-4 mb-4">
            <Card style={{ width: '18rem', margin: '20px' }} className="mx-auto text-center">
              <Card.Body>
                <Card.Title style={{ fontSize: '20px', marginBottom: '15px', fontWeight: 'bold' }}></Card.Title>
                <Card.Text style={{ fontSize: '18px', marginBottom: '10px' }}>
                  <p>Total Price: ${totalPrice}</p>
                  <p>Total Books to Buy: {totalBooks}</p>
                </Card.Text>
                <Button onClick={() => setCart(prevCart => prevCart.filter(item => item.id !== b.id))}>
                  Remove from Cart
                </Button>
              </Card.Body>
            </Card>
          </div>
        ))}
        <Button variant="success" onClick={handleButtonClick}>
          Continue
        </Button>
      </div>
    </div>
  );
}

export default BookingComponent;
