// CartComponent.js
import React, { useState } from "react";
import { Button, Card, Row } from "react-bootstrap";
import { useNavigate, useParams } from "react-router";
import Navbarcomponent from "../navbar";
import axios from "axios";
import TotalInfoCard from "./TotalInfoCard"; // Assuming you have TotalInfoCard component
import CartItem from "./cartItem";

function CartComponent() {
  const { cid } = useParams();
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [errorMsg, setErrorMsg] = useState("");

  const removeFromCart = (bookToRemove) => {
    setCart((prevCart) => prevCart.filter((book) => book.id !== bookToRemove.id));
  };

  const handleButtonClick = async () => {
    try {
      const customerId = cid; // Assuming you have the customer ID from useParams

      const response = await axios.post(`http://localhost:8182/customerBook/customerid/${customerId}`, {
        books: cart,
      });

      if (response.status === 200) {
        // Successfully created customer books
        setCart([]); // Clear the cart after purchase
        navigate("/booking");
      } else {
        console.error(`Error: ${response.data}`);
      }
    } catch (error) {
      console.error(`Error: ${error.message}`);
      setErrorMsg("Error updating book, please try again..");
    }
  };

  const totalPrice = cart.reduce((sum, book) => sum + book.bookPrice, 0);
  const totalBooks = cart.length;

  return (
    <div>
      <Navbarcomponent />
      <br />
      <h2>Cart</h2>
      <br />
      <br />
      <br />
      <br />
      <div className="container col-md-8">
        <Row>
          {cart.map((book, index) => (
            <div key={index} className="col-md-4 mb-4">
              <CartItem book={book} removeFromCart={removeFromCart} />
            </div>
          ))}
          <div className="col-md-4 mb-4 mx-auto d-flex justify-content-center">
            <TotalInfoCard totalBooks={totalBooks} totalPrice={totalPrice} />
          </div>
          <button
            type="button"
            onClick={handleButtonClick}
            className="btn btn-success btn-icon-text bouncebutton"
            style={{ fontSize: '30px' }}
          >
            <i className="fa fa-check btn-icon-prepend"></i>Proceed to Purchase
          </button>
        </Row>
      </div>
    </div>
  );
}

export default CartComponent;
