import { Col, Form, Nav, NavDropdown, Navbar, Row } from "react-bootstrap";
import React, { useState } from "react";
import { useNavigate } from "react-router";
import axios from "axios";

function AdminDashboard() {
  const [bookTitle, setBookTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [rating, setRating] = useState('');
  const [isbn, setIsbn] = useState('');
  const [bookDesc, setBookDesc] = useState('');
  const [authorDesc, setAuthorDesc] = useState('');
  const [bookPrice, setBookPrice] = useState('');
  const [noOfCopies, setNoOfCopies] = useState('');
  const [catid, setCatid] = useState(/* initial value */);
  const [id, setId] = useState(/* initial value */);

  const [book, setBook] = useState({});
  const navigate = useNavigate();
  const [msg, setMsg] = useState('');

  const AddBook = (e) => {
    e.preventDefault();

    let categoryObj = {
      "bookTitle": bookTitle,
      "author": author,
      "rating": rating,
      "isbn": isbn,
      "bookDesc": bookDesc,
      "authorDesc": authorDesc,
      "bookPrice": bookPrice,
      "noOfCopies": noOfCopies
    };

    axios.post(`http://localhost:8182/Book/add/${catid}/${id}`)
      .then(response => {
        setBook(response.data);
        navigate('customer/dashboard');
      })
      .catch(function (error) {
        setMsg('nothing to show');
      });
  };

  return (
    <div>
      <Navbar bg="dark" data-bs-theme="dark" style={{ padding: 10 }}>
        <Navbar.Brand ><h2>INFOLIBRA</h2></Navbar.Brand>
        <Nav className="me-auto  mr-4">
          <Nav.Link >Admin Dashboard</Nav.Link>
          <Nav.Link >Orders</Nav.Link>
        </Nav>
        <Navbar.Collapse className="justify-content-end">
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          {
            localStorage.getItem('isLoggedIn') ?
              <React.Fragment>
                <Navbar.Text >
                  Signed in as: <span style={{ color: "white" }}>
                    {localStorage.getItem('username')}
                  </span>
                </Navbar.Text>
                &nbsp;&nbsp;&nbsp;
                <button className="btn btn-info btn-sm ml-4" onClick={() => {
                  localStorage.clear();
                  navigate('/auth/login?msg=you have logged out..')
                }}>Logout</button>
              </React.Fragment>
              :
              <button className="btn btn-primary" onClick={() => navigate('/auth/login')}>Login</button>
          }
        </Navbar.Collapse>
      </Navbar>
      <NavDropdown title="Options" id="basic-nav-dropdown">
        <NavDropdown.Item onClick={AddBook}>Add</NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Delete clicked")}>
          Delete
        </NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Update clicked")}>
          Update
        </NavDropdown.Item>
        <NavDropdown.Item onClick={() => console.log("Status clicked")}>
          Status
        </NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item
          onClick={() => {
            localStorage.clear();
            navigate("/auth/login?msg=you have logged out..");
          }}
        >
          Logout
        </NavDropdown.Item>
      </NavDropdown>
    </div>
  );
}

export default AdminDashboard;
