import React, { useState, useEffect } from "react";
import axios from "axios";
import { Card, Form } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";
import AdminNavbar from "./adminnavbar";

function AdminDashboard() {
  const { id } = useParams();
  const [books, setBooks] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [customerBookings, setCustomerBookings] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
    fetchCustomers();
    fetchCustomerBookings();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get("http://localhost:8182/Book/all");
      setBooks(response.data);
    } catch (error) {
      console.error("Error fetching books:", error.message);
    }
  };

  const fetchCustomers = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customer/getall");
      setCustomers(response.data);
    } catch (error) {
      console.error("Error fetching customers:", error.message);
    }
  };

  const fetchCustomerBookings = async () => {
    try {
      const response = await axios.get("http://localhost:8182/customerBook/getall");
      setCustomerBookings(response.data);
    } catch (error) {
      console.error("Error fetching customer bookings:", error.message);
    }
  };

  const navigateToUpdateBook = (bookId) => {
    navigate(`/admin/update/${bookId}`);
  };

  const filterData = (data, query) => {
    return data.filter(item => {
      const lowercaseQuery = query.toLowerCase();
      return (
        (item.bookTitle && item.bookTitle.toLowerCase().includes(lowercaseQuery)) ||
        (item.author && item.author.toLowerCase().includes(lowercaseQuery)) ||
        (item.name && item.name.toLowerCase().includes(lowercaseQuery)) ||
        (item.city && item.city.toLowerCase().includes(lowercaseQuery)) ||
        (item.contact && item.contact.toLowerCase().includes(lowercaseQuery)) ||
        (item.issueDate && item.issueDate.toLowerCase().includes(lowercaseQuery)) ||
        (item.returnDate && item.returnDate.toLowerCase().includes(lowercaseQuery)) ||
        (item.amount && item.amount.toString().toLowerCase().includes(lowercaseQuery)) ||
        (item.isbn && item.isbn.toLowerCase().includes(lowercaseQuery))
      );
    });
  };

  const renderCardDetails = (item) => {
    return (
      <Card key={item.id} className="m-2" style={{ width: "18rem" }}>
        <Card.Body>
          <Card.Title>{item.bookTitle || item.name || (item.customer && item.customer.name)}</Card.Title>
          {item.author && <Card.Text><strong>Author:</strong> {item.author}</Card.Text>}
          {item.bookPrice && <Card.Text><strong>Price:</strong> {item.bookPrice}</Card.Text>}
          {item.isbn && <Card.Text><strong>ISBN:</strong> {item.isbn}</Card.Text>}
          {item.city && <Card.Text><strong>City:</strong> {item.city}</Card.Text>}
          {item.contact && <Card.Text><strong>Contact:</strong> {item.contact}</Card.Text>}
          {item.issueDate && <Card.Text><strong>Issue Date:</strong> {item.issueDate}</Card.Text>}
          {item.returnDate && <Card.Text><strong>Return Date:</strong> {item.returnDate}</Card.Text>}
          {item.amount && <Card.Text><strong>Fine Amount:</strong> {item.amount}</Card.Text>}
          {item.id && <Card.Text><strong>Book id:</strong> {item.id}</Card.Text>}
        </Card.Body>
      </Card>
    );
  };

  const filteredItems = filterData([...books, ...customers, ...customerBookings], searchQuery);

  return (
    <div>
      <AdminNavbar />

      <div className="container mt-5">
        <h1 className="mb-4">Admin Dashboard</h1>

        <Form.Group controlId="formSearch">
          <Form.Control
            type="text"
            placeholder="Search..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </Form.Group>
        <br />

        {filteredItems.map(renderCardDetails)}
      </div>
    </div>
  );
}

export default AdminDashboard;
