import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';

function BookingComponent({ cart }) {
  const history = useHistory();
  const totalPrice = cart.reduce((sum, book) => sum + book.bookPrice, 0);
  const totalBooks = cart.length;

  const handleContinue = () => {
    // Navigate to the BookingBook component or any other route you want
    history.push('/BookingBook');
  };

  return (
    <div className='container mb-4 d-flex justify-content-center align-items-center' style={{ minHeight: '100vh' }}>
      <div>
        <h1>Booking Info...</h1>
        <Card style={{ width: '18rem', margin: '20px' }}>
          <Card.Body>
            <Card.Title style={{ fontSize: '20px', marginBottom: '15px', fontWeight: 'bold' }}></Card.Title>
            <Card.Text style={{ fontSize: '18px', marginBottom: '10px' }}>
              <p>Total Price: ${totalPrice}</p>
              <p>Total Books to Buy: {totalBooks}</p>
            </Card.Text>
            <Button variant="success" onClick={handleContinue}>
              Cancel
            </Button>
            &nbsp;&nbsp;
            <Button variant="success" onClick={handleContinue}>
              Continue
            </Button>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}

export default BookingComponent;
