import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Table } from 'react-bootstrap';
import AdminNavbar from './adminnavbar';
import axios from 'axios';

function CustomersTable() {
  // Replace this with the actual logic to display the customers table
  return (
    <div>
      <h2>Customers Table</h2>
      <Table striped bordered hover>
        {/* Add the table headers and rows for customers */}
      </Table>
    </div>
  );
}

function BooksTable() {
  // Replace this with the actual logic to display the books table
  return (
    <div>
      <h2>Books Table</h2>
      <Table striped bordered hover>
        {/* Add the table headers and rows for books */}
      </Table>
    </div>
  );
}

function VSComponent() {
  const [showCustomersTable, setShowCustomersTable] = useState(false);
  const [showBooksTable, setShowBooksTable] = useState(false);
  const [customersData, setCustomersData] = useState([]);
  const [booksData, setBooksData] = useState([]);

  const handleCustomersClick = async () => {
    setShowCustomersTable(true);
    setShowBooksTable(false);

    try {
      // Make GET request to fetch customer data
      const response = await axios.get('http://localhost:8182/customerBook/bookid/9');
      setCustomersData(response.data); // Assuming the API returns an array of customer data
    } catch (error) {
      console.error(`Error fetching customers: ${error.message}`);
    }
  };

  const handleBooksClick = async () => {
    setShowBooksTable(true);
    setShowCustomersTable(false);

    try {
      // Make GET request to fetch book data
      const response = await axios.get('http://localhost:8182/customerBook/customerid/15');
      setBooksData(response.data); // Assuming the API returns an array of book data
    } catch (error) {
      console.error(`Error fetching books: ${error.message}`);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <Container fluid>
        <Row>
          <Col md={3}>
            <h2>Overview</h2>
            <Button onClick={handleCustomersClick}>Customers</Button><br /><br />
            <Button onClick={handleBooksClick}>Books</Button>
          </Col>
          <Col md={9}>
            <Row>
              <Col>
                <Form>
                  <Form.Group className="mb-3">
                    <Form.Control type="text" placeholder="Search" />
                  </Form.Group>
                </Form>
              </Col>
            </Row>
            <Row>
              <Col>
                <h4>No. of Customers: {customersData.length}</h4>
              </Col>
              <Col>
                <h4>No. of Books: {booksData.length}</h4>
              </Col>
            </Row>
            {showCustomersTable && <CustomersTable />}
            {showBooksTable && <BooksTable />}
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default VSComponent;
