import React, { useEffect, useState } from "react";
import Navbarcomponent from "../navbar";
import axios from "axios";
import { Row } from "react-bootstrap";
import { useParams } from "react-router";

function BookingStatus() {
  const [customerBook, setCustomerBook] = useState([]);
  const { id } = useParams();
  const [totalFine, setTotalFine] = useState(0);

  useEffect(() => {
    axios.get(`http://localhost:8182/customerBook/customerid/${id}`)
      .then((response) => {
        console.log("API response:", response.data);
        setCustomerBook(response.data);

        // Calculate total fine
        const total = response.data.reduce((acc, cb) => acc + cb.amount, 0);
        setTotalFine(total);
      })
      .catch((error) => console.error("Error fetching customer books:", error));
  }, [id]);

  const handleReviewClick = (bookingId) => {
    // Add logic to handle the review button click for the specific bookingId
    console.log(`Review button clicked for booking ID ${bookingId}`);
    // Add your logic to show/hide textarea or open a modal for review
  };

  return (
    <div>
      <Navbarcomponent />
      <Row>
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>ID</th>
              <th>Issue Date</th>
              <th>Return Date</th>
              <th>Fine</th>
              <th>Total Fine</th>
              <th>Add review</th>
            </tr>
          </thead>
          <tbody>
            {customerBook.map((cb, index) => (
              <tr key={index}>
                <td>{cb.customer.name}</td>
                <td>{cb.id}</td>
                <td>{cb.issueDate}</td>
                <td>{cb.returnDate}</td>
                <td>{cb.amount}</td>
                <td>{totalFine}</td>
                <td>
                  <button onClick={() => handleReviewClick(cb.id)}>Review</button>
                  {/* Add textarea and form for updating review */}
                  <form style={{ display: "none" }}>
                    <textarea rows="3" cols="30" placeholder="Enter your review"></textarea>
                    <button type="submit">Submit Review</button>
                  </form>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </Row>
    </div>
  );
}

export default BookingStatus;
