import React, { useEffect, useState } from "react";
import Navbarcomponent from "../navbar";
import axios from "axios";
import { Card, Row } from "react-bootstrap";
import { useParams } from "react-router";

function BookingStatus() {
  const [customerBook, setCustomerBook] = useState([]);
  const { id } = useParams();
  const [totalBooks, setTotalBooks] = useState(0);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    axios.get(`http://localhost:8182/customerBook/customerid/${id}`)
      .then((response) => {
        console.log("API response:", response.data);
        setCustomerBook(response.data);
        calculateTotals(response.data);
      })
      .catch((error) => console.error("Error fetching customer books:", error));
  }, [id]);

  const calculateTotals = (data) => {
    let books = 0;
    let amount = 0;

    data.forEach((cb) => {
      books += cb.noOfBooks;
      amount += cb.amount;
    });

    setTotalBooks(books);
    setTotalAmount(amount);
  };

  return (
    <div>
      <Navbarcomponent />
      <Row>
        {customerBook.map((cb, index) => (
          <div key={index} className="col-md-4 mb-4">
            <Card style={{ 
              width: "400px", 
              border: "1px solid #dee2e6", 
              borderRadius: "0.25rem", 
              marginBottom: "20px",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)", 
              transition: "transform 0.3s ease-in-out", 
              cursor: "pointer",
            }}
            onMouseEnter={(e) => e.target.style.transform = "scale(1.05)"}
            onMouseLeave={(e) => e.target.style.transform = "scale(1)"}
            >
              <Card.Body>
                <Card.Title style={{ 
                  fontSize: "1.5rem", 
                  fontWeight: "bold", 
                  marginBottom: "10px",
                  color: "#3498db",
                }}>{cb.customer.name}</Card.Title>
                <hr />
                <div>
                  <div>
                    <span style={{ fontWeight: "bold" }}>Id :</span> {cb.id}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold" }}>Issue Date:</span> {cb.issueDate}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold" }}>Return Date:</span> {cb.returnDate}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold" }}>Fine:</span> ${cb.amount}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold" }}>No of Books:</span> {cb.noOfBooks}
                  </div>
                  <div>
                    <span style={{ fontWeight: "bold" }}>Total Cost:</span> ${cb.totalCost}
                  </div>
                </div>
              </Card.Body>
            </Card>
          </div>
        ))}
      </Row>
      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <h4>Total Books: {totalBooks}</h4>
        <h4>Total Amount: ${totalAmount}</h4>
      </div>
    </div>
  );
}

export default BookingStatus;
