import { useEffect, useState } from "react";

import Login from "../Auth/login/login";
import Books from "./book";

import { Link, useNavigate, useSearchParams } from "react-router-dom";
import NavbarComponent from "../navbar";
import axios from "axios";


import { Button, Card, CardHeader, Image, ListGroup, ListGroupItem, Nav, Row } from "react-bootstrap";

function CustomerDashboard() {
  const navigate = useNavigate();
  const [param] = useSearchParams();
  const [qStr, setQstr] = useState('');
  const [book, setBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const [category, setCategory] = useState([]);
 

  useEffect(() => {
    axios.get('http://localhost:8182/Book/all')
      .then(response => setBooks(response.data))
     
      axios.get('http://localhost:8182/category/getall')
      .then(response => setCategories(response.data))
      .catch(error => setMsg('Error in Fetching categories'));
  },[ ])
  const redirectToBookDetails = (bookId) => {
    navigate(`/Book/getwithauthdesc?id=${bookId}`);
  };




  const searchBooks = (str) => {
    console.log('search func in parent comp called.....' + str)
    setQstr(str);
  };

  const headingStyle = {
    fontFamily: 'Verdana, sans-serif',
    fontSize: '28px',
    color: '#4CAF50', // Green color
    fontWeight: 'bold',
    margin: '10px 0',
  };


  const getbookcategory = async (id) => {
    
      
      setLoading(true);
      const response = await axios.get(`http://localhost:8182/customer/getbycategoryid?id=${id}`);

      console.log('API response:', response);
      setBooks(response.data || []);
     
    
  };





  // const process = () => {
  //     if (!param.get('page')) {
  //         return (
  //             <div>
  //                 <h2 style={headingStyle}>WELCOME TO INFOLIBRA</h2>
  //                 <HomeComponent strVal={qStr} />
  //             </div>
  //         );
  //     }
  //     if (param.get('page') === 'books') {
  //         return (
  //             <div>
  //                 <h2 style={headingStyle}>Books Page</h2>
  //                 <Books />
  //             </div>
  //         );
  //     }
  //     if (param.get('page') === 'cart') {
  //         return (
  //             <div>
  //                 <h1 style={{ color: '#FF5733' }}>Cart Page</h1>
  //             </div>
  //         );
  //     }
  //     if (param.get('page') === 'previous_orders') {
  //         if (localStorage.getItem('isLoggedIn') === null) {
  //             localStorage.setItem('url', '/customer/dashboard?page=previous_orders');
  //             return (
  //                 <div>
  //                     <h2 style={headingStyle}>Login Required</h2>
  //                     <Login />
  //                 </div>
  //             );
  //         }
  //         return (
  //             <div>
  //                 <h2 style={headingStyle}>Previous Orders Page</h2>
  //                 <h1 style={{ color: '#3366CC' }}>Previous Orders Content</h1>
  //             </div>
  //         );
  //     }
  // };

  return (
    <div >
      <NavbarComponent func={searchBooks} />
      {/* {process()} */}
      <Row style={{ padding: 20 }}>
    <div className="col-md-4">
    <Card style={{ width: "27rem", margin: 20 }}>
            <Card.Header style={{ backgroundColor: "#2c3e50", color: "#ecf0f1" }}>
              <h3 style={{ fontSize: "1.5rem", fontFamily: "Arial, sans-serif" }}>Categories</h3>
            </Card.Header>
            <ListGroup>
              {categories.map((c, index) => (
                <div key={index}>
                  <ListGroup.Item>
                    <Nav.Link onClick={() => { getbookcategory(c.id); }}>
                      <h4 style={{ color: "#34495e", fontWeight: "bold", fontFamily: "Verdana, sans-serif" }}>
                        {c.name}
                      </h4>
                    </Nav.Link>
                  </ListGroup.Item>
                </div>
              ))}
            </ListGroup>
          </Card>
        </div>

        <div className="col-md-8">
          <Row>
            {book.map((b, index) => (
              <div key={index} className="col-md-4 mb-4">
                <Card style={{ width: '400px', backgroundColor: 'rgba(255, 255, 255, 0.9)' }}>
                  <Card.Body>
                    <Link to={`/books/${b.id}`}>
                      <Card.Title style={{ color: "#e74c3c", fontSize: "1.2rem", fontFamily: "Georgia, serif" }}>
                        {b.bookTitle}
                      </Card.Title>
                    </Link>
                    <hr />
                  </Card.Body>
                  <div>
                    <div style={{ display: 'flex', flexDirection: 'column', marginBottom: '10px' }}>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#27ae60" }}>Rating:</span> {b.rating}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#3498db" }}>Author:</span>
                        <Link to={`/author/${b.author}`} style={{ color: "#3498db", textDecoration: "none", fontFamily: "Helvetica, sans-serif" }}>
                          {b.author}
                        </Link>
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#e67e22" }}>Category:</span> {b.category.name}
                      </div>
                      <div>
                        <span style={{ fontWeight: 'bold', color: "#f39c12" }}>Price:</span> {b.bookPrice}
                      </div>
                    </div>
                    <Button style={{ width: 200, alignSelf: "center", margin: 15, backgroundColor: "#3498db", color: "#fff", fontFamily: "Arial, sans-serif" }} variant="outline-primary" href='/auth/login'>Add to cart</Button>
                  </div>
                </Card>



          </div>
        ))}
          </Row>
        </div>
      </Row>
    </div>
  );
}

export default CustomerDashboard;
